// 排序, 双指针
func threeSum(nums []int) [][]int {
	if len(nums) < 3 {
		return [][]int{}
	}
	// 升序排列
	sort.Ints(nums)
	var res [][]int

	for i := 0; i < len(nums)-2; i++ {
		var curr = nums[i]

		if i > 0 && curr == nums[i-1] {
			continue
		}

		if curr > 0 {
			return res
		}

		var l = i + 1
		var r = len(nums) - 1
		for {
			if l >= r {
				break
			}

			if nums[r] < 0 {
				return res
			}

			var n = curr + nums[l] + nums[r]
			if n < 0 {
				l++
			} else if n > 0 {
				r--
			} else /*if n == 0*/ {
				res = append(res, []int{curr, nums[l], nums[r]})
				l++
				for nums[l-1] == nums[l] && l < r {
					l++
				}
			}
		}
	}
	return res
}
